import React, { useState } from 'react'

import UserList from './components/UserList/UserList'
import NewUser from './components/NewUser/NewUser'
import ErrorModal from './components/ErrorModal/ErrorModal'

function App() {
  const DUMMY_USERS_DATA = []

  const [userData, setUserData] = useState(DUMMY_USERS_DATA)
  const [isValidValues, setIsValidValues] = useState(true)

  const addNewUserHandler = (savedUserData) => {
    setUserData((prevUserData) => {
      return [savedUserData, ...prevUserData]
    })
  }

  const isValidUserData = (isValid) => {
    setIsValidValues(isValid)
  }

  return (
    <div>
      {!isValidValues && <ErrorModal />}
      {isValidValues && (
        <NewUser
          onAddNewUser={addNewUserHandler}
          onIsValidUserData={isValidUserData}
        />
      )}
      <UserList userData={userData} />
    </div>
  )
}

export default App
