import React from 'react'

import UserForm from './UserForm/UserForm'

const NewUser = (props) => {
  const saveUserDadaHandler = (entredUserData) => {
    const savedUserData = {
      ...entredUserData,
      id: Math.random(),
    }

    const isValid = !(
      savedUserData.name.trim().length === 0 ||
      savedUserData.age.length === 0 ||
      savedUserData.age <= 0
    )

    props.onAddNewUser(savedUserData)
    props.onIsValidUserData(isValid)
  }

  return (
    <div>
      <UserForm onSaveUserDada={saveUserDadaHandler}></UserForm>
    </div>
  )
}

export default NewUser
